CREATE TABLE "Authors"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"FirstName" NVARCHAR2(100), 
	"LastName" NVARCHAR2(100), 
	"Status" NUMBER(1,0) NOT NULL ENABLE, 
    CONSTRAINT "PK_Authors" PRIMARY KEY ("Id")
) 
CREATE TABLE "Books"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"Name" NVARCHAR2(100), 
	"NumberOfPage" NUMBER NOT NULL ENABLE, 
	"Image" NVARCHAR2(100), 
	"Status" NUMBER(1,0) NOT NULL ENABLE, 
	"AuthorId" NUMBER NOT NULL ENABLE, 
	"TypeId" NUMBER NOT NULL ENABLE, 
    CONSTRAINT "PK_Books" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Books_Authors_AuthorId" FOREIGN KEY ("AuthorId")
        REFERENCES "Authors" ("Id") ON DELETE CASCADE ENABLE, 
    CONSTRAINT "FK_Books_Types_TypeId" FOREIGN KEY ("TypeId")
        REFERENCES "Types" ("Id") ON DELETE CASCADE ENABLE
)
CREATE TABLE "BorrowedBooks"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"BorrowDate" TIMESTAMP (7) NOT NULL ENABLE, 
	"ReturnDate" TIMESTAMP (7) NOT NULL ENABLE, 
	"Status" NUMBER NOT NULL ENABLE, 
	"UserId" NUMBER NOT NULL ENABLE, 
	"BookId" NUMBER NOT NULL ENABLE, 
    CONSTRAINT "PK_BorrowedBooks" PRIMARY KEY ("Id"), 
    CONSTRAINT "FK_BorrowedBooks_Books_BookId" FOREIGN KEY ("BookId")
        REFERENCES "Books" ("Id") ON DELETE CASCADE ENABLE, 
    CONSTRAINT "FK_BorrowedBooks_Users_UserId" FOREIGN KEY ("UserId")
        REFERENCES "Users" ("Id") ON DELETE CASCADE ENABLE
)
CREATE TABLE "Messages"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"Text" NVARCHAR2(100), 
	"Date" TIMESTAMP (7) NOT NULL ENABLE, 
	"UserID" NUMBER NOT NULL ENABLE, 
	"Status" NUMBER(1,0) DEFAULT 0, 
    CONSTRAINT "PK_Messages" PRIMARY KEY ("Id"), 
    CONSTRAINT "FK_Messages_Users_UserID" FOREIGN KEY ("UserID")
        REFERENCES "Users" ("Id") ON DELETE CASCADE ENABLE
)
CREATE TABLE "Positions"(
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"Name" NVARCHAR2(100), 
	"Status" NUMBER(1,0) NOT NULL ENABLE, 
    CONSTRAINT "PK_Positions" PRIMARY KEY ("Id")
)
CREATE TABLE "Types"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"Name" NVARCHAR2(100), 
	"Status" NUMBER(1,0) NOT NULL ENABLE, 
	 CONSTRAINT "PK_Types" PRIMARY KEY ("Id")
)
CREATE TABLE "Users"(	
    "Id" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 INCREMENT BY 1 START WITH 1 NOT NULL ENABLE, 
	"FirstName" NVARCHAR2(100), 
	"LastName" NVARCHAR2(100), 
	"UserName" NVARCHAR2(100), 
	"Password" NVARCHAR2(100), 
	"Email" NVARCHAR2(100), 
	"Status" NUMBER(1,0) NOT NULL ENABLE, 
	"PositionId" NUMBER(10,0) NOT NULL ENABLE, 
    CONSTRAINT "PK_Users" PRIMARY KEY ("Id"), 
    CONSTRAINT "FK_Users_Positions_PositionId" FOREIGN KEY ("PositionId")
        REFERENCES"Positions" ("Id") ON DELETE CASCADE ENABLE
)